function GoBackCon({onClick}) {
  return (
    <div style={{ display: "flex", justifyContent: "flex-start" }}>
      <div onClick={onClick} style={{ padding: "10px 0px", cursor: "pointer" }}>
        <GoBack />
      </div>
    </div>
  );
}

const ModalWrapper = ({ style, children, className, showClose, showCancel, isModalVisible, setIsModalVisible, showCloseAction= () => setIsModalVisible(false) }) => {
  // const [isModalVisible, setIsModalVisible] = useState(true);

  // const showModal = () => {
  //   setIsModalVisible(true);
  // };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      <Modal
        footer={null}
        title={null}
        centered={true}
        closable={false}
        style={style}
        // closeIcon={<GoBack />}
        visible={isModalVisible}
        // onOk={handleOk}
        onCancel={handleCancel}
        className={className}
      >
        {showCancel && (
          <div
            onClick={() => setIsModalVisible(false)}
            className={styles.cancel}
          >
            <Cancel />
          </div>
        )}
        {showClose !== "no" && <GoBackCon onClick={showCloseAction} />}
        {children}
      </Modal>
    </>
  );
};

export default ModalWrapper;
